#!/bin/bash

# Railway Deployment Monitor
# Monitors Railway deployment logs until successful

set -e

echo "🚀 Railway Deployment Monitor"
echo "=============================="
echo ""

# Check Railway CLI
if ! command -v railway &> /dev/null; then
    echo "❌ Railway CLI not found"
    echo "   Install: npm i -g @railway/cli"
    exit 1
fi

# Check if logged in
if ! railway whoami &> /dev/null; then
    echo "⚠️  Not logged in to Railway"
    echo ""
    echo "Please login first:"
    echo "  railway login"
    echo ""
    echo "Then link your project:"
    echo "  railway link"
    echo ""
    exit 1
fi

# Check if project is linked
if ! railway status &> /dev/null; then
    echo "⚠️  No Railway project linked"
    echo ""
    echo "Link your project:"
    echo "  railway link"
    echo ""
    exit 1
fi

echo "✅ Railway CLI ready"
echo ""

# Get service info
echo "📋 Service Information:"
railway status
echo ""

# Get service URL
SERVICE_URL=$(railway domain 2>/dev/null | head -n 1 || echo "")
if [ -n "$SERVICE_URL" ]; then
    echo "🌐 Service URL: $SERVICE_URL"
    echo ""
else
    echo "⚠️  Could not get service URL"
    echo ""
fi

# Monitor logs
echo "📊 Monitoring Deployment Logs..."
echo "   (Press Ctrl+C to stop)"
